"use strict";function getPopularMovies(){$.ajax({url:"/Helper/GetPopularMovies",success:function(n){console.log(n);let t=n.results;$("body").append(`<div style = "margin-bottom:50px;">  
                         <h1>Movie of the Day</h1>
                         <p>${t[0].original_title}</p>
                         <img src = "https://image.tmdb.org/t/p/w500${t[0].poster_path}">
                     </div>`);for(let n=1;n<9;n++)$("body").append(`<div style = "margin-bottom:20px;">  
                            <h2>${t[n].original_title}</h2>
                            <img src = "https://image.tmdb.org/t/p/w500${t[n].poster_path}">
                        </div>`)},error:function(){console.log("Error!!!")}})}function displayRating(){$.ajax({url:"/Helper/GetMovie",success:function(n){console.log(n);let t=n.vote_average;$(".rating").append(`<p>${t}</p>`)},error:function(){console.log("Error!!!")}})}function setRating(n){console.log(n);let t=$(n).data("userid"),i=$(n).data("movieidapi");$.ajax({url:`/Helper/SetRating?user_id=${t}&movie_id=${i}`})}function addToWatchlist(n){console.log(n);let t=$(n).data("userid"),i=$(n).data("movieidapi");$.ajax({url:`/Helper/AddToWatchlist?user_id=${t}&movie_idapi=${i}`})}function addToFavorite(n){console.log(n);let t=$(n).data("userid"),i=$(n).data("movieidapi");$.ajax({url:`/Helper/AddToFavorite?user_id=${t}&movie_idapi=${i}`})}function addToWatched(n){console.log(n);let t=$(n).data("userid"),i=$(n).data("movieidapi");$.ajax({url:`/Helper/AddToWatched?user_id=${t}&movie_idapi=${i}`})}function addToNotify(n){console.log(n);let t=$(n).data("userid"),i=$(n).data("movieidapi");$.ajax({url:`/Helper/AddToNotify?user_id=${t}&movie_idapi=${i}`})}$(document).ready(function(){getPopularMovies();displayRating();$("a.no_click").click(function(n){n.preventDefault()})});